# WAP to calculate all even and odd number between 100 seprately.
def sum():
 odd=0
 even=0
 i=1
 while i<=100:
    if i%2==0:
       even+=i
    else:
        odd +=i
    i=i+1 
 print("sum of all even number between  is ",even)
 print("sum of all odd number between  is ", odd)
sum()

     
         

